INSERT INTO `events` (`id`, `name`, `url_friendly_name`, `start_date`, `end_date`, `description`, `stub`, `href`, `tz_continent`, `tz_place`, `attendee_count`, `attending`, `event_average_rating`, `event_comments_count`, `tracks_count`, `talks_count`, `icon`, `location`, `images`, `tags`, `created`, `modified`) VALUES ('1', 'PHP UK Conference 2019', 'php-uk-conference-2019', '2019-02-20 00:00:00', '2019-02-22 23:59:59', 'PHP UK is pleased to announce the 14th Annual PHP UK conference; a 2-day event with 3 great tracks held at at The Brewery, in the heart of the City of London.\n\nWith over 700 delegates, speakers, and sponsors, PHP UK conference aims to deliver fantastic up-to-date content about PHP and related web technologies in a comfortable and professional setting. There are countless networking opportunities to engage with international speakers and delegates, which makes the event one you won\'t want to miss.', NULL, 'https://www.phpconference.co.uk/', 'Europe', 'London', '25', '0', '0', '0', '0', '32', 'phpuk.jpg', 'The Brewery', '[]', '[\"technology\"]', '2019-02-21 12:51:22', '2019-02-21 12:51:22');

